  #------------------------------------------------------------------
  # Tests ihit before dhit
  #------------------------------------------------------------------

org   0x0000

ori $1,$0,0xdddd
ori $2, $0, 0x100
ori $3, $0,0x800
ori $4, $0,0x80c

loop:
    sw    $1,0($3)
    addi $3,$3, 4 
    bne $3, $4,loop

 
  halt      

  org   0x00F0
  cfw   0x7337
  cfw   0x2701
  cfw   0x1337
